var mysql = require('mysql') ;
var db = mysql.createConnection({
    host: "localhost",
    user: "root",
    password: "Iamhappy2018@",
    database: "tray"
  }); 
// console.log("helo")

db.connect(function (err) {
  if (err) {
      throw err
  };
  console.log("Connected!");
  db.query("SELECT * FROM accounts1", function (err, result, fields) {
      if (err) {
          throw err
      };
  })
});
module.exports.db = db
