import React from 'react';
import logo from './logo.svg';
import './App.css';
import Register from './views/register/index'
import Login from './views/login/index'
// import Session from './sessions/closure.js'
import Home from './views/home/index'
import Styles from './main.module.css'

var axios  = require('axios')
// console.log(Session)
class App extends React.Component{
    constructor(){
      super();
      this.state = {
        loggedIn:0,
        username:'',
        userId:''

      }
    }
  componentDidMount = ()=>{
    let user  = JSON.parse(localStorage.getItem('user'));

    if(user){
      this.setState(
        {
          loggedIn:1,
          username:user.username,
          userId:user.userId
      }

      )
    }

  }
  logout = ()=>{
    this.setState({
      loggedIn:0,
      username:'',
      userId:''
    })
    localStorage.removeItem('user');
  }
  render(){
    return (
      <div className="App">
          <button className={Styles.button} onClick={this.logout}> LOGOUT</button>
          {(this.state.loggedIn) ?  (<Home username = {this.state.username } isLoggedIn = {this.updateOnLogin}></Home>) : (<Login></Login>)}
      </div>
   )
    }
}

export default App;
